mapper.select:
select 
from baby_member.t_alabao_turn_out 
where 1=1

			 and card_holder=#{cardHolder}


			 and bank_name=#{bankName}


			 and turn_out_status=#{turnOutStatus}


			 and card_no=#{cardNo}


			 and account=#{account}



			and DATE_FORMAT(create_time,'%Y-%m-%d') >= DATE_FORMAT(#{createTimeFrom}, '%Y-%m-%d')




				and DATE_FORMAT(create_time,'%Y-%m-%d') <= DATE_FORMAT(#{createTimeTo}, '%Y-%m-%d')



	limit #{start},#{limit}

select count(1) from baby_member.t_alabao_turn_out


			 and card_holder=#{cardHolder}


			 and bank_name=#{bankName}


			 and turn_out_status=#{turnOutStatus}


			 and card_no=#{cardNo}


			 and account=#{account}



			and DATE_FORMAT(create_time,'%Y-%m-%d') >= DATE_FORMAT(#{createTimeFrom}, '%Y-%m-%d')




				and DATE_FORMAT(create_time,'%Y-%m-%d') <= DATE_FORMAT(#{createTimeTo}, '%Y-%m-%d')
