mapper.select:

select 
	a.id,a.label_code,a.sku_code , disabled, a.show_order, create_time
from 
	t_sku_label_relation a 


		and a.label_code = #{labelCode}


		and a.sku_code = #{skuCode}


		and a.disabled = #{disabled}



order by case when  a.show_order=0 or a.show_order is null then 9999999 else a.show_order end

	LIMIT #{start}


	,#{limit}

select 
	count(a.id)
from 
	t_sku_label_relation as a


		and a.sku_code = #{skuCode}


		and a.label_code = #{labelCode}


		and a.disabled = #{disabled}

select 
	id,label_code,sku_code , disabled, show_order, create_time
from 
	t_sku_label_relation a


		and a.sku_code = #{skuCode}


		and a.label_code = #{labelCode}


		and a.disabled = #{disabled}



	LIMIT #{start},#{limit}
